mapper.select:
SELECT m.id as meiwenId, m.type, m.title, m.summary, m.cover_img_url, m.content, m.browse_count, m.collect_count,
c.collect as is_collect,
m.comment_count, m.create_time, m.update_time, u.user_id, u.username, u.photo_url FROM meiwen m LEFT JOIN jj_user u ON m.user_id = u.user_id
LEFT JOIN mw_collect c ON m.id = c.meiwen_id AND c.user_id = #{userId}
WHERE m.id = #{id}
order by m.create_time desc

SELECT m.id, m.meiwen_id, m.content, m.create_time, u.user_id, u.username, u.photo_url FROM mw_comment m LEFT JOIN jj_user u ON m.user_id = u.user_id WHERE m.meiwen_id = #{meiwenId}
order by m.create_time desc
LIMIT #{m},#{n}
